function [P,dPdF,dPdp,g,dgdF] = Deriv(F11,F12,F21,F22,pres,mu,wcase)
% See symb.m for the models

detF = (F11.*F22 - F12.*F21);


switch (wcase)
    case 1
        P = [F11.*mu + F22.*pres - (F22.*mu)./(detF),...
             F12.*mu - F21.*pres + (F21.*mu)./(detF),...
             F21.*mu - F12.*pres + (F12.*mu)./(detF),...
             F22.*mu + F11.*pres - (F11.*mu)./(detF)];
         
        dPdF = [mu + (F22.^2.*mu)./(detF).^2,...
                -(F21.*F22.*mu)./(detF).^2,...
                -(F21.*F22.*mu)./(detF).^2,...
                mu + (F21.^2.*mu)./(detF).^2,...
                -(F12.*F22.*mu)./(detF).^2,...
                (F11.*F22.*mu)./(detF).^2 - pres,...
                pres + (F12.*F21.*mu)./(detF).^2,...
                -(F11.*F21.*mu)./(detF).^2,...
                -(F12.*F22.*mu)./(detF).^2,...
                pres + (F12.*F21.*mu)./(detF).^2,...
               (F11.*F22.*mu)./(detF).^2 - pres,...
               -(F11.*F21.*mu)./(detF).^2,...
                mu + (F12.^2.*mu)./(detF).^2,...
               -(F11.*F12.*mu)./(detF).^2,...
               -(F11.*F12.*mu)./(detF).^2,...
                 mu + (F11.^2.*mu)./(detF).^2]; 
             
         dPdp = [F22, -F21, -F12,  F11];
         
         g = detF - 1;
         
         dgdF = [F22, -F21, -F12,  F11];         
    case 2
        P = [F11.*mu - (F22.*mu)./(detF) + (F22.*pres)./(detF),...
             F12.*mu + (F21.*mu)./(detF) - (F21.*pres)./(detF),...
             F21.*mu + (F12.*mu)./(detF) - (F12.*pres)./(detF),...
             F22.*mu - (F11.*mu)./(detF) + (F11.*pres)./(detF)];
         
        dPdF = [ mu + (F22.^2.*mu - F22.^2.*pres)./(detF).^2,...
                   -(F21.*F22.*(mu - pres))./(detF).^2,...
                   -(F21.*F22.*(mu - pres))./(detF).^2,...
                 mu + (F21.^2.*mu - F21.^2.*pres)./(detF).^2,...
                   -(F12.*F22.*(mu - pres))./(detF).^2,...
                    (F11.*F22.*(mu - pres))./(detF).^2,...
                    (F12.*F21.*(mu - pres))./(detF).^2,...
                   -(F11.*F21.*(mu - pres))./(detF).^2,...
                   -(F12.*F22.*(mu - pres))./(detF).^2,...
                    (F12.*F21.*(mu - pres))./(detF).^2,...
                    (F11.*F22.*(mu - pres))./(detF).^2,...
                   -(F11.*F21.*(mu - pres))./(detF).^2,...
                 mu + (F12.^2.*mu - F12.^2.*pres)./(detF).^2,...
                   -(F11.*F12.*(mu - pres))./(detF).^2,...
                   -(F11.*F12.*(mu - pres))./(detF).^2,...
                 mu + (F11.^2.*mu - F11.^2.*pres)./(detF).^2]; 
             
        dPdp = [F22./(detF),...
               -F21./(detF),...
               -F12./(detF),...
                F11./(detF)];
        
        g = log(detF);
        
        dgdF = [F22./(detF),...
               -F21./(detF),...
               -F12./(detF),...
                F11./(detF)];
      
    case 3
        P = [F22.*pres + (mu.*((2.*F11)./(detF).^(2./3) - (2.*F22.*(F11.^2 + F12.^2 + F21.^2 + F22.^2 + 1))./(3.*(detF).^(5./3))))./2,...
            (mu.*((2.*F12)./(detF).^(2./3) + (2.*F21.*(F11.^2 + F12.^2 + F21.^2 + F22.^2 + 1))./(3.*(detF).^(5./3))))./2 - F21.*pres,...
            (mu.*((2.*F21)./(detF).^(2./3) + (2.*F12.*(F11.^2 + F12.^2 + F21.^2 + F22.^2 + 1))./(3.*(detF).^(5./3))))./2 - F12.*pres,...
             F11.*pres + (mu.*((2.*F22)./(detF).^(2./3) - (2.*F11.*(F11.^2 + F12.^2 + F21.^2 + F22.^2 + 1))./(3.*(detF).^(5./3))))./2];
 
        dPdF = [(mu.*(2.*F11.^2.*F22.^2 - 6.*F11.*F12.*F21.*F22 + 9.*F12.^2.*F21.^2 + 5.*F12.^2.*F22.^2 + 5.*F21.^2.*F22.^2 + 5.*F22.^4 + 5.*F22.^2))./(9.*(detF).^(8./3)),...
               -(mu.*(6.*F11.*F12.*F21.^2 - F11.^2.*F21.*F22 + 6.*F11.*F12.*F22.^2 - F12.^2.*F21.*F22 + 5.*F21.^3.*F22 + 5.*F21.*F22.^3 + 5.*F21.*F22))./(9.*(detF).^(8./3)),...
               -(mu.*(6.*F11.*F12.*F21.^2 - F11.^2.*F21.*F22 + 6.*F11.*F12.*F22.^2 - F12.^2.*F21.*F22 + 5.*F21.^3.*F22 + 5.*F21.*F22.^3 + 5.*F21.*F22))./(9.*(detF).^(8./3)),...
                (mu.*(5.*F11.^2.*F21.^2 + 9.*F11.^2.*F22.^2 - 6.*F11.*F12.*F21.*F22 + 2.*F12.^2.*F21.^2 + 5.*F21.^4 + 5.*F21.^2.*F22.^2 + 5.*F21.^2))./(9.*(detF).^(8./3)),...
               -(mu.*(6.*F11.*F12.^2.*F21 - F11.^2.*F12.*F22 + 6.*F11.*F21.*F22.^2 + 5.*F12.^3.*F22 - F12.*F21.^2.*F22 + 5.*F12.*F22.^3 + 5.*F12.*F22))./(9.*(detF).^(8./3)),...
                (2.*F12.^2.*mu)./(3.*(detF).^(5./3)) - pres + (2.*F21.^2.*mu)./(3.*(detF).^(5./3)) + (mu.*(F11.^2 + F12.^2 + F21.^2 + F22.^2 + 1))./(3.*(detF).^(5./3)) + (5.*F12.*F21.*mu.*(F11.^2 + F12.^2 + F21.^2 + F22.^2 + 1))./(9.*(detF).^(8./3)),...
                pres - (2.*F11.^2.*mu)./(3.*(detF).^(5./3)) - (2.*F22.^2.*mu)./(3.*(detF).^(5./3)) - (mu.*(F11.^2 + F12.^2 + F21.^2 + F22.^2 + 1))./(3.*(detF).^(5./3)) + (5.*F11.*F22.*mu.*(F11.^2 + F12.^2 + F21.^2 + F22.^2 + 1))./(9.*(detF).^(8./3)),...
               -(mu.*(5.*F11.^3.*F21 + 6.*F11.^2.*F12.*F22 - F11.*F12.^2.*F21 + 5.*F11.*F21.^3 - F11.*F21.*F22.^2 + 5.*F11.*F21 + 6.*F12.*F21.^2.*F22))./(9.*(detF).^(8./3)),...
               -(mu.*(6.*F11.*F12.^2.*F21 - F11.^2.*F12.*F22 + 6.*F11.*F21.*F22.^2 + 5.*F12.^3.*F22 - F12.*F21.^2.*F22 + 5.*F12.*F22.^3 + 5.*F12.*F22))./(9.*(detF).^(8./3)),...
                pres - (2.*F11.^2.*mu)./(3.*(detF).^(5./3)) - (2.*F22.^2.*mu)./(3.*(detF).^(5./3)) - (mu.*(F11.^2 + F12.^2 + F21.^2 + F22.^2 + 1))./(3.*(detF).^(5./3)) + (5.*F11.*F22.*mu.*(F11.^2 + F12.^2 + F21.^2 + F22.^2 + 1))./(9.*(detF).^(8./3)),...
                (2.*F12.^2.*mu)./(3.*(detF).^(5./3)) - pres + (2.*F21.^2.*mu)./(3.*(detF).^(5./3)) + (mu.*(F11.^2 + F12.^2 + F21.^2 + F22.^2 + 1))./(3.*(detF).^(5./3)) + (5.*F12.*F21.*mu.*(F11.^2 + F12.^2 + F21.^2 + F22.^2 + 1))./(9.*(detF).^(8./3)),...
               -(mu.*(5.*F11.^3.*F21 + 6.*F11.^2.*F12.*F22 - F11.*F12.^2.*F21 + 5.*F11.*F21.^3 - F11.*F21.*F22.^2 + 5.*F11.*F21 + 6.*F12.*F21.^2.*F22))./(9.*(detF).^(8./3)),...
                (mu.*(5.*F11.^2.*F12.^2 + 9.*F11.^2.*F22.^2 - 6.*F11.*F12.*F21.*F22 + 5.*F12.^4 + 2.*F12.^2.*F21.^2 + 5.*F12.^2.*F22.^2 + 5.*F12.^2))./(9.*(detF).^(8./3)),...
               -(mu.*(5.*F11.^3.*F12 + 6.*F11.^2.*F21.*F22 + 5.*F11.*F12.^3 - F11.*F12.*F21.^2 - F11.*F12.*F22.^2 + 5.*F11.*F12 + 6.*F12.^2.*F21.*F22))./(9.*(detF).^(8./3)),...
               -(mu.*(5.*F11.^3.*F12 + 6.*F11.^2.*F21.*F22 + 5.*F11.*F12.^3 - F11.*F12.*F21.^2 - F11.*F12.*F22.^2 + 5.*F11.*F12 + 6.*F12.^2.*F21.*F22))./(9.*(detF).^(8./3)),...
                (mu.*(5.*F11.^4 + 5.*F11.^2.*F12.^2 + 5.*F11.^2.*F21.^2 + 2.*F11.^2.*F22.^2 + 5.*F11.^2 - 6.*F11.*F12.*F21.*F22 + 9.*F12.^2.*F21.^2))./(9.*(detF).^(8./3))];
 
         dPdp = [F22, -F21, -F12,  F11];
         
         g = detF - 1;
         
         dgdF = [F22, -F21, -F12,  F11];         
         
    case 4
        P = [(mu.*((2.*F11)./(detF).^(2./3) - (2.*F22.*(F11.^2 + F12.^2 + F21.^2 + F22.^2 + 1))./(3.*(detF).^(5./3))))./2 + (F22.*pres)./(detF),...
             (mu.*((2.*F12)./(detF).^(2./3) + (2.*F21.*(F11.^2 + F12.^2 + F21.^2 + F22.^2 + 1))./(3.*(detF).^(5./3))))./2 - (F21.*pres)./(detF),...
             (mu.*((2.*F21)./(detF).^(2./3) + (2.*F12.*(F11.^2 + F12.^2 + F21.^2 + F22.^2 + 1))./(3.*(detF).^(5./3))))./2 - (F12.*pres)./(detF),...
             (mu.*((2.*F22)./(detF).^(2./3) - (2.*F11.*(F11.^2 + F12.^2 + F21.^2 + F22.^2 + 1))./(3.*(detF).^(5./3))))./2 + (F11.*pres)./(detF)];
        
        dPdF = [(5.*F22.^2.*mu + 5.*F22.^4.*mu - 9.*F22.^2.*pres.*(detF).^(2./3) + 2.*F11.^2.*F22.^2.*mu + 9.*F12.^2.*F21.^2.*mu + 5.*F12.^2.*F22.^2.*mu + 5.*F21.^2.*F22.^2.*mu - 6.*F11.*F12.*F21.*F22.*mu)./(9.*(detF).^(8./3)),...
                (F21.*F22.*pres)./(detF).^2 - (mu.*((4.*F12.*F22)./(3.*(detF).^(5./3)) - (4.*F11.*F21)./(3.*(detF).^(5./3)) + (10.*F21.*F22.*(F11.^2 + F12.^2 + F21.^2 + F22.^2 + 1))./(9.*(detF).^(8./3))))./2,...
                (F21.*F22.*pres)./(detF).^2 - (mu.*((4.*F12.*F22)./(3.*(detF).^(5./3)) - (4.*F11.*F21)./(3.*(detF).^(5./3)) + (10.*F21.*F22.*(F11.^2 + F12.^2 + F21.^2 + F22.^2 + 1))./(9.*(detF).^(8./3))))./2,...
                (5.*F21.^2.*mu + 5.*F21.^4.*mu - 9.*F21.^2.*pres.*(detF).^(2./3) + 5.*F11.^2.*F21.^2.*mu + 9.*F11.^2.*F22.^2.*mu + 2.*F12.^2.*F21.^2.*mu + 5.*F21.^2.*F22.^2.*mu - 6.*F11.*F12.*F21.*F22.*mu)./(9.*(detF).^(8./3)),...
                (F12.*F22.*pres)./(detF).^2 - (mu.*((4.*F21.*F22)./(3.*(detF).^(5./3)) - (4.*F11.*F12)./(3.*(detF).^(5./3)) + (10.*F12.*F22.*(F11.^2 + F12.^2 + F21.^2 + F22.^2 + 1))./(9.*(detF).^(8./3))))./2,...
                (3.*F11.*F22.*mu - 9.*pres.*(detF).^(5./3) + 2.*F12.*F21.*mu + 3.*F11.*F22.^3.*mu - 4.*F12.*F21.^3.*mu + 3.*F11.^3.*F22.*mu - 4.*F12.^3.*F21.*mu - 9.*F12.*F21.*pres.*(detF).^(2./3) + 2.*F11.^2.*F12.*F21.*mu + 9.*F11.*F12.^2.*F22.*mu + 9.*F11.*F21.^2.*F22.*mu + 2.*F12.*F21.*F22.^2.*mu)./(9.*(detF).^(8./3)),...
                (9.*pres.*(detF).^(5./3) + 2.*F11.*F22.*mu + 3.*F12.*F21.*mu - 4.*F11.*F22.^3.*mu + 3.*F12.*F21.^3.*mu - 4.*F11.^3.*F22.*mu + 3.*F12.^3.*F21.*mu - 9.*F11.*F22.*pres.*(detF).^(2./3) + 9.*F11.^2.*F12.*F21.*mu + 2.*F11.*F12.^2.*F22.*mu + 2.*F11.*F21.^2.*F22.*mu + 9.*F12.*F21.*F22.^2.*mu)./(9.*(detF).^(8./3)),...
                (F11.*F21.*pres)./(detF).^2 - (mu.*((4.*F11.*F12)./(3.*(detF).^(5./3)) - (4.*F21.*F22)./(3.*(detF).^(5./3)) + (10.*F11.*F21.*(F11.^2 + F12.^2 + F21.^2 + F22.^2 + 1))./(9.*(detF).^(8./3))))./2,...
                (F12.*F22.*pres)./(detF).^2 - (mu.*((4.*F21.*F22)./(3.*(detF).^(5./3)) - (4.*F11.*F12)./(3.*(detF).^(5./3)) + (10.*F12.*F22.*(F11.^2 + F12.^2 + F21.^2 + F22.^2 + 1))./(9.*(detF).^(8./3))))./2,...
                (9.*pres.*(detF).^(5./3) + 2.*F11.*F22.*mu + 3.*F12.*F21.*mu - 4.*F11.*F22.^3.*mu + 3.*F12.*F21.^3.*mu - 4.*F11.^3.*F22.*mu + 3.*F12.^3.*F21.*mu - 9.*F11.*F22.*pres.*(detF).^(2./3) + 9.*F11.^2.*F12.*F21.*mu + 2.*F11.*F12.^2.*F22.*mu + 2.*F11.*F21.^2.*F22.*mu + 9.*F12.*F21.*F22.^2.*mu)./(9.*(detF).^(8./3)),...
                (3.*F11.*F22.*mu - 9.*pres.*(detF).^(5./3) + 2.*F12.*F21.*mu + 3.*F11.*F22.^3.*mu - 4.*F12.*F21.^3.*mu + 3.*F11.^3.*F22.*mu - 4.*F12.^3.*F21.*mu - 9.*F12.*F21.*pres.*(detF).^(2./3) + 2.*F11.^2.*F12.*F21.*mu + 9.*F11.*F12.^2.*F22.*mu + 9.*F11.*F21.^2.*F22.*mu + 2.*F12.*F21.*F22.^2.*mu)./(9.*(detF).^(8./3)),...
                (F11.*F21.*pres)./(detF).^2 - (mu.*((4.*F11.*F12)./(3.*(detF).^(5./3)) - (4.*F21.*F22)./(3.*(detF).^(5./3)) + (10.*F11.*F21.*(F11.^2 + F12.^2 + F21.^2 + F22.^2 + 1))./(9.*(detF).^(8./3))))./2,...
                (5.*F12.^2.*mu + 5.*F12.^4.*mu - 9.*F12.^2.*pres.*(detF).^(2./3) + 5.*F11.^2.*F12.^2.*mu + 9.*F11.^2.*F22.^2.*mu + 2.*F12.^2.*F21.^2.*mu + 5.*F12.^2.*F22.^2.*mu - 6.*F11.*F12.*F21.*F22.*mu)./(9.*(detF).^(8./3)),...
                (F11.*F12.*pres)./(detF).^2 - (mu.*((4.*F11.*F21)./(3.*(detF).^(5./3)) - (4.*F12.*F22)./(3.*(detF).^(5./3)) + (10.*F11.*F12.*(F11.^2 + F12.^2 + F21.^2 + F22.^2 + 1))./(9.*(detF).^(8./3))))./2,...
                (F11.*F12.*pres)./(detF).^2 - (mu.*((4.*F11.*F21)./(3.*(detF).^(5./3)) - (4.*F12.*F22)./(3.*(detF).^(5./3)) + (10.*F11.*F12.*(F11.^2 + F12.^2 + F21.^2 + F22.^2 + 1))./(9.*(detF).^(8./3))))./2,...
                (5.*F11.^2.*mu + 5.*F11.^4.*mu - 9.*F11.^2.*pres.*(detF).^(2./3) + 5.*F11.^2.*F12.^2.*mu + 5.*F11.^2.*F21.^2.*mu + 2.*F11.^2.*F22.^2.*mu + 9.*F12.^2.*F21.^2.*mu - 6.*F11.*F12.*F21.*F22.*mu)./(9.*(detF).^(8./3))];
  
        dPdp = [F22./(detF),...
               -F21./(detF),...
               -F12./(detF),...
                F11./(detF)];
        
        g = log(detF);
        
        dgdF = [F22./(detF),...
               -F21./(detF),...
               -F12./(detF),...
                F11./(detF)];        
    otherwise
        error('Not a valid model');
end


% function [P,dPdF] = Deriv(F11,F12,F21,F22,mu,kappa,wcase)
% % See symb.m for the models
% 
% detF = (F11.*F22 - F12.*F21);
% 
% switch (wcase)
%     case 1
% 
%     P = [F11.*mu - F22.*kappa.*(F12.*F21 - F11.*F22 + 1) - (F22.*mu)./(detF),...
%          F12.*mu + F21.*kappa.*(F12.*F21 - F11.*F22 + 1) + (F21.*mu)./(detF),...
%          F21.*mu + F12.*kappa.*(F12.*F21 - F11.*F22 + 1) + (F12.*mu)./(detF),...
%          F22.*mu - F11.*kappa.*(F12.*F21 - F11.*F22 + 1) - (F11.*mu)./(detF)];
% 
%     dPdF = [                                                 mu + F22.^2.*kappa + (F22.^2.*mu)./(detF).^2,... 
%                                                             - F21.*F22.*kappa - (F21.*F22.*mu)./(detF).^2,...
%                                                             - F21.*F22.*kappa - (F21.*F22.*mu)./(detF).^2,...
%                                                              mu + F21.^2.*kappa + (F21.^2.*mu)./(detF).^2,...
%                                                             - F12.*F22.*kappa - (F12.*F22.*mu)./(detF).^2,...
%      kappa.*(F12.*F21 - F11.*F22 + 1) + mu./(detF) + F12.*F21.*kappa + (F12.*F21.*mu)./(detF).^2,...
%      F11.*F22.*kappa - mu./(detF) - kappa.*(F12.*F21 - F11.*F22 + 1) + (F11.*F22.*mu)./(detF).^2,...
%                                                             - F11.*F21.*kappa - (F11.*F21.*mu)./(detF).^2,...
%                                                             - F12.*F22.*kappa - (F12.*F22.*mu)./(detF).^2,...
%      F11.*F22.*kappa - mu./(detF) - kappa.*(F12.*F21 - F11.*F22 + 1) + (F11.*F22.*mu)./(detF).^2,...
%      kappa.*(F12.*F21 - F11.*F22 + 1) + mu./(detF) + F12.*F21.*kappa + (F12.*F21.*mu)./(detF).^2,...
%                                                             - F11.*F21.*kappa - (F11.*F21.*mu)./(detF).^2,...
%                                                              mu + F12.^2.*kappa + (F12.^2.*mu)./(detF).^2,...
%                                                             - F11.*F12.*kappa - (F11.*F12.*mu)./(detF).^2,...
%                                                             - F11.*F12.*kappa - (F11.*F12.*mu)./(detF).^2,...
%                                                              mu + F11.^2.*kappa + (F11.^2.*mu)./(detF).^2];
%                                                                                                           
%     case 2
%         
%     P = [F11.*mu - (F22.*mu)./(detF) + (F22.*kappa.*log(detF))./(detF),...
%          F12.*mu + (F21.*mu)./(detF) - (F21.*kappa.*log(detF))./(detF),...
%          F21.*mu + (F12.*mu)./(detF) - (F12.*kappa.*log(detF))./(detF),...
%          F22.*mu - (F11.*mu)./(detF) + (F11.*kappa.*log(detF))./(detF)];
% 
% 
%     dPdF = [mu + (F22.^2.*kappa + F22.^2.*mu - F22.^2.*kappa.*log(detF))./(detF).^2,...
%                  -(F21.*F22.*(kappa + mu - kappa.*log(detF)))./(detF).^2,...
%                  -(F21.*F22.*(kappa + mu - kappa.*log(detF)))./(detF).^2,...
%      mu + (F21.^2.*kappa + F21.^2.*mu - F21.^2.*kappa.*log(detF))./(detF).^2,...
%                  -(F12.*F22.*(kappa + mu - kappa.*log(detF)))./(detF).^2,...
%           (F11.*F22.*(mu - kappa.*log(detF)) + F12.*F21.*kappa)./(detF).^2,...
%           (F12.*F21.*(mu - kappa.*log(detF)) + F11.*F22.*kappa)./(detF).^2,...
%                  -(F11.*F21.*(kappa + mu - kappa.*log(detF)))./(detF).^2,...
%                  -(F12.*F22.*(kappa + mu - kappa.*log(detF)))./(detF).^2,...
%           (F12.*F21.*(mu - kappa.*log(detF)) + F11.*F22.*kappa)./(detF).^2,...
%           (F11.*F22.*(mu - kappa.*log(detF)) + F12.*F21.*kappa)./(detF).^2,...
%                  -(F11.*F21.*(kappa + mu - kappa.*log(detF)))./(detF).^2,...
%      mu + (F12.^2.*kappa + F12.^2.*mu - F12.^2.*kappa.*log(detF))./(detF).^2,...
%                  -(F11.*F12.*(kappa + mu - kappa.*log(detF)))./(detF).^2,...
%                  -(F11.*F12.*(kappa + mu - kappa.*log(detF)))./(detF).^2,...
%      mu + (F11.^2.*kappa + F11.^2.*mu - F11.^2.*kappa.*log(detF))./(detF).^2];
% 
%     case 3
% 
%     P =  [(mu.*((2.*F11)./(detF).^(2./3) - (2.*F22.*(F11.^2 + F12.^2 + F21.^2 + F22.^2 + 1))./(3.*(detF).^(5./3))))./2 - F22.*kappa.*(F12.*F21 - F11.*F22 + 1),...
%           (mu.*((2.*F12)./(detF).^(2./3) + (2.*F21.*(F11.^2 + F12.^2 + F21.^2 + F22.^2 + 1))./(3.*(detF).^(5./3))))./2 + F21.*kappa.*(F12.*F21 - F11.*F22 + 1),...
%           (mu.*((2.*F21)./(detF).^(2./3) + (2.*F12.*(F11.^2 + F12.^2 + F21.^2 + F22.^2 + 1))./(3.*(detF).^(5./3))))./2 + F12.*kappa.*(F12.*F21 - F11.*F22 + 1),...
%           (mu.*((2.*F22)./(detF).^(2./3) - (2.*F11.*(F11.^2 + F12.^2 + F21.^2 + F22.^2 + 1))./(3.*(detF).^(5./3))))./2 - F11.*kappa.*(F12.*F21 - F11.*F22 + 1)];
